mapper.select:
select c_id,n_code,c_zwmc,n_gzxz,n_yx,n_gzjy,n_xlyq,c_zwms,
c_gzdz,c_jsjlyx,n_llcs,n_zt,dt_fbsj,dt_fbsj from db_test.t_jobinfo 

	n_yxzt=1
	and n_zt=2

	AND dt_fbsj > #{dt_fbsj}


		AND	c_zwmc like '%${c_nr}%'


		AND	n_gzjy=#{n_gzjy}


		AND n_xlyq >=#{n_xlyq}


		AND n_gzxz=#{n_gzxz}


		AND n_yx > #{n_yxstart}


		AND n_yx < #{n_yxend}


	limit #{pagesize,jdbcType=INTEGER} 
	offset #{offset,jdbcType=INTEGER};

select count(*) from db_test.t_jobinfo 

	n_yxzt=1
	and n_zt=2

		AND dt_fbsj > #{dt_fbsj}


		AND	c_zwmc like '%${c_nr}%'


		AND	n_gzjy=#{n_gzjy}


		AND n_xlyq=#{n_xlyq}


		AND n_gzxz=#{n_gzxz}


		AND n_yx > #{n_yxstart}


		AND n_yx < #{n_yxend}
